"""Working Directory Example"""
import os
import sys

sys.path.append(os.path.dirname(os.path.dirname(os.path.dirname(os.path.dirname(os.path.dirname(os.path.abspath(__file__)))))))

from agentbay import AgentBay
from agentbay import CreateSessionParams


def main():
    print("=== Working Directory ===\n")
    client = AgentBay()
    session = None

    try:
        session_result = client.create(CreateSessionParams(image_id="linux_latest"))
        session = session_result.session

        # Check current directory
        result = session.command.execute_command("pwd")
        print(f"1. Current dir: {result.output}")

        # Change directory
        result = session.command.execute_command("cd /tmp && pwd")
        print(f"2. After cd: {result.output}")

        # Create and navigate
        result = session.command.execute_command("mkdir -p /tmp/mydir && cd /tmp/mydir && pwd")
        print(f"3. New dir: {result.output}")

        print("\n=== Completed ===")

    finally:
        if session:
            client.delete(session)


if __name__ == "__main__":
    main()

