def _set_low_android_resolution(session) -> None:
    """
    Reduce Android resolution to make screenshots smaller for tests.
    """
    cmds = [
        "wm size 720x1280",
        "wm density 160",
    ]
    for cmd in cmds:
        r = session.command.execute_command(cmd)
        assert r.success, f"Command failed: {cmd} error={r.error_message}"


def _prepare_for_screenshot_tests(session) -> None:
    _set_low_android_resolution(session)
    start = session.mobile.start_app("monkey -p com.android.settings 1")
    assert start.success, f"Failed to start Settings: {start.error_message}"
    time.sleep(2)


